mapper.update:
UPDATE whopuppy.article
SET title = #{title}, content = #{content}, region = #{region}, thumbnail = #{thumbnail}, is_posted = 1
WHERE id = #{id};

    UPDATE whopuppy.article_image
    SET is_use = 1
    WHERE id = #{i.id}

mapper.select:
SELECT
,
, b.board as B_board FROM whopuppy.article AS a JOIN whopuppy.user AS u ON a.user_id = u.id JOIN whopuppy.board AS b ON a.board_id = b.id WHERE a.board_id = #{boardId} AND a.is_posted = 1 AND a.is_deleted = 0
AND a.region = #{region}
LIMIT #{cursor},#{limit}
